            }
        }

        let moduleData = {
            code: newModuleCode,
            title: moduleName,
            school: school,
            reviewers: reviewersArray,
            firstReviewer: firstReviewer,
            questions: allQuestions
        };
        if (editForm) {
           fetch("/api/update-module", {
            method: 'POST',
