import React, {useEffect, useState} from 'react';
import { useNavigate } from "react-router-dom";
import { useLocation } from 'react-router-dom';


export default function ChatPage() {
    const [chatLogs, setChatLogs] = useState([]);
    const [loginType, setLoginType] = useState([]);
    const [userID, setUserID] = useState([]);
    const [requestID, setRequestID] = useState([]);
    useEffect(() => {
        setLoginType(sessionStorage.getItem("LoginType"));
        setRequestID(sessionStorage.getItem("ChatRequestID"));
        setUserID(sessionStorage.getItem("Username"));
        fetch("/api/get-chat-logs", {
            method: 'POST',
            body: JSON.stringify({data: [sessionStorage.getItem("ChatRequestID")]}),
            headers: {
                'Content-Type': 'application/json'
            }
        })
        .then(response => response.json())
        .then(data => {
            setChatLogs(data[0]);
        })
        .catch(error => console.error(error))
    }, []);

    function DisplayPreviousChat() {
        let rows = [];
        if (chatLogs.chat === null) {
            return <div>Start the discussion</div>
        }
        for (let chat of chatLogs) {
            rows.push(
                <diV>
                    <div>{chat.sender} on {chat.sent.day}/{chat.sent.month}/{chat.sent.year} at {chat.sent.hour}:{chat.sent.minutes}</div>
                    <div>{chat.message}</div> 
                </diV>

            )
        }
        return rows;
    }

    function AddChat() {
        var currentDate = new Date();
        let newChat = document.getElementById("chatEnter").value;
        let dateTime = {
            day: currentDate.getDay(),
            month: (currentDate.getMonth() + 1),
            year: currentDate.getFullYear(),
            hour: currentDate.getHours(),
            minutes: currentDate.getMinutes()
        }
        let data = {
            sender: userID,
            message: newChat,
            sent: dateTime
        }
        let chat = [];
        if (chatLogs.chat === null) {
            chat.push(data);
        }
        else {
            chat = Array.from(chatLogs); 
            chat.push(data); 
        }
        fetch("/api/add-new-chat", {
            method: 'POST',
            body: JSON.stringify({data: [requestID, chat]}),
            headers: {
                'Content-Type': 'application/json'
            }
        })
        .then(response => response.json())
        .then(data => {
            console.log(data);
        })
        .catch(error => console.error(error))
        setChatLogs(chat);
    }
    return <div>
        <div>
            <h1>Conversation </h1>
            <DisplayPreviousChat/>
            <input type="text" id ="chatEnter" placeholder="Chat" required/>
            <button type="button" id="button" onClick={AddChat}>Send</button>
        </div>
    </div>;
}
