import { useNavigate } from "react-router-dom";
import React, {useEffect, useState} from 'react';


export default function LoginPage() {
    const navigate = useNavigate();

    function checkDetails() {
        let username = document.getElementById("usernameEnter").value;
        let password = document.getElementById("passwordEnter").value;
        let loginType = document.getElementById("loginTypeSelect").value;
        console.log("username: " + username + " password: " + password + " login type: " + loginType);

        if (username.split("@")[1] === "st-andrews.ac.uk") {
            fetch("/api/check-login-detail", {
                method: 'POST',
                body: JSON.stringify({data: [username, password, loginType]}),
                headers: {
                    'Content-Type': 'application/json'
                }
            })
            .then(response => response.json())
            .then(data => {
                if (data === "VALID") {
                    sessionStorage.setItem("Username", username);
                    sessionStorage.setItem("Password", password);
                    sessionStorage.setItem("LoginType", loginType);
                    switch (loginType) {
                        case "Student":
                            navigate("/student-home-page");
                            break;
                        case "Staff": 
                            navigate("/staff-home-page");
                            break;
                    }
                }
                else if (data === "INVALID") {
                    alert("Invalid credentials");
                }
            })
            .catch(error => console.error(error))
        }
    }
    return <div className="login-page">
        <div>
            <h1>Extension Request <br></br> System Login</h1>
            <h2></h2>
            <p>Username: </p>
            <input type="text" id ="usernameEnter" placeholder="Username" required/>
            <p>Password</p>
            <input type="password" id ="passwordEnter" placeholder="Password" required/>
            <p></p>
            <select id="loginTypeSelect">
                <option value="Student">Student</option>
                <option value="Staff">Staff</option>
            </select>
            <button type="button" id="button" onClick={checkDetails}>Login</button>

        </div>
    </div>;
}
