import React, {useEffect, useState} from 'react';
import { useNavigate } from "react-router-dom";
import { useLocation } from 'react-router-dom';
//import './style.css';


export default function CreateNewModulePage() {
    const [allRequest, setAllRequest] = useState([]);
    const [staffID, setStaffID] = useState([]);
    useEffect(() => {
        setStaffID(sessionStorage.getItem("Username"));
        fetch("/api/get-pending-staff-decisions", {
            method: 'POST',
            body: JSON.stringify({data: [sessionStorage.getItem("Username").split("@")[0]]}),
            headers: {
                'Content-Type': 'application/json'
            }
        })
        .then(response => response.json())
        .then(data => {
            setAllRequest(data);
        });
    }, []);

    function DisplayReuqests() {
        let rows = [];
        console.log(allRequest);
        for (let request of allRequest) {
            rows.push(<div>Module: {request.moduleCode}</div>);
            rows.push(<div>Student ID: {request.studentID}</div>);
            let requestQuestions = JSON.parse(request.answers).questions;
            let requestAnswers = JSON.parse(request.answers).answers;
            for (let i = 0; i < requestQuestions.length; i++) {
                rows.push(<div>{requestQuestions[i]} : {requestAnswers[i]}</div>);
            }
            rows.push(<button type="button" id="button" >Approve</button>);
            rows.push(<button type="button" id="button" >Contact</button>);
            rows.push(<button type="button" id="button" >Reject</button>);
            rows.push(<button type="button" id="button" >Forward</button>);
            console.log(JSON.parse(request.answers));
        }
        return rows;
    }
    return <div classname="/staff-decision-page">
        <div>
            <h1>Pending Requests In Your Modules</h1>
            <DisplayReuqests/>
        </div>
    </div>
}
