import React, {useEffect, useState} from 'react';
import { useNavigate } from "react-router-dom";
import { useLocation } from 'react-router-dom';


export default function StudentHistory() {
    const navigate = useNavigate();
    const [studentRequest, setStudentRequest] = useState([]);
    const [studentID, setStudentID] = useState([]);
    const [requestData, setRequestData] = useState([]);

    useEffect(() => {
        setStudentRequest(sessionStorage.getItem("studentRequest"));
        setStudentID(sessionStorage.getItem("studentID"));

        fetch("/api/get-student-history", {
            method: "POST",
            body: JSON.stringify({data: sessionStorage.getItem("studentID")}),
            headers: {
                'Content-Type': 'application/json'
            }
        })
        .then(response => response.json())
        .then(data => {
            setRequestData(data);
        })
        .catch(error => console.error(error))
    }, []);

    function EnterChat(requestID) {
        console.log(requestID);
        //sessionStorage.setItem("ChatRequestID", )
        //navigate("/chat-page");
        // Navigate;
    }

    function DisplayRequests() {
        let rows = [];
        for (let request of requestData) {
            rows.push(<div>Module: {request.moduleCode}</div>)
            let requestQuestions = JSON.parse(request.answers).questions;
            let requestAnswers = JSON.parse(request.answers).answers;
            rows.push(<br></br>);
            rows.push(<div>Question | Answer</div>)
            for (let i = 0; i <requestQuestions.length; i++) {
                if (typeof requestAnswers[i] === "object") {
                    const answerString = requestAnswers[i].join(' | ');
                    rows.push(<div>{requestQuestions[i]} : {answerString}</div>)
                }
                else {
                    rows.push(<div>{requestQuestions[i]} : {requestAnswers[i]}</div>)
                }

            }
            rows.push(<br></br>);
            rows.push(<div>Status: {request.status}</div>)
            rows.push(<br></br>);
            if (request.status === "Contact" && studentRequest) {
                rows.push(<button type="button" id="button" onClick={() => EnterChat(request.requestID)} >Enter Chat</button>)
            }
        }
        return rows;
    }

    return <div className = "student-history-page">
        <div>
            <h1>Request history for {studentID}</h1>
            <DisplayRequests/>
        </div>


    </div>
}
