import React, {useEffect, useState} from 'react';
import { useLocation } from 'react-router-dom';
import { useNavigate } from "react-router-dom";
//import './style.css';


export default function NewRequest() {
    const navigate = useNavigate();
    const [name, setName] = useState([]);
    const [moduleTitle, setModuleTtile] = useState([]);
    const [school, setSchool] = useState([]);
    const [questionData, setQuestionData] = useState([]);
    const [username, setUsername] = useState([]);
    const [module, setModule] = useState([]);

    useEffect(() => {
        setUsername(sessionStorage.getItem("Username"));
        setModule(sessionStorage.getItem("module"));

        fetch("/api/get-name", {
            method: 'POST',
            body: JSON.stringify({data: [sessionStorage.getItem("Username"), sessionStorage.getItem("Password"), sessionStorage.getItem("LoginType")]}),
            headers: {
                'Content-Type': 'application/json'
            }
        })
        .then(response => response.json())
        .then(data => {
            if (data === "INVALID") {
                alert("Access Denied.");
            }
            else {
                setName(data);
            }
        })
        .catch(error => console.error(error))

        fetch("/api/get-module-data", {
            method: 'POST',
            body: JSON.stringify({data: sessionStorage.getItem("module")}),
            headers: {
                'Content-Type': 'application/json'
            }
        })
        .then(response => response.json())
        .then(data => {
            setModuleTtile(data[0].name);
            setSchool(data[0].school);
            setQuestionData(data[0].requestData);
        })
    }, []);

    function GenerateQuestions() {
        let rows = [];
        for (let question of questionData) {
            console.log(question);
            rows.push(
                <div>{question.question}</div>
            )
        }
        return rows;
    }

    return <div className="student-home-page">
        <div>
            <h1>Creating New Extension Request For {module}: {moduleTitle}</h1>
            <p>In the school of {school}</p>
            <GenerateQuestions/>
        </div>
    </div>;
}
